import React, { useEffect, useState } from 'react'
import { Button, Image, Upload } from 'antd'
import { addListItemService, getItemService, updateListItemService } from '../services'
import { useUI } from '../UIProvider'
import { fileToBase64, getNormalizedFile, handleError } from '../helpers'
import PropTypes from '../PropTypes'
import heic2any from 'heic2any'
import lists from '../lists'
import { useUpdateEffect } from 'usehooks-ts'

const formMode = {
  view: 'view',
  edit: 'edit'
}

interface Props {
  storeID: string | number
  setStoreID: (args: any) => void
  listSharepointName: string
  width?: string | number
  mode?: 'view' | 'edit'
}

const ImageBase64Store = ({
  storeID,
  setStoreID,
  listSharepointName,
  width = 200,
  mode = 'edit'
}: Props) => {
  // state
  const [base64Image, setBase64Image] = useState()
  const [loadingUploadFile, setLoadingUploadFile] = useState(false)
  const [loadingGetImage, setLoadingGetImage] = useState(true)

  // hook
  const ui = useUI()

  const getBase64Image = async () => {
    if (!storeID) {
      setLoadingGetImage(false)
      return
    }

    setLoadingGetImage(true)

    try {
      const storeData = await getItemService(lists.Base64Store, storeID)

      setBase64Image(storeData.Base64_Content)
    } catch (error) {
      handleError(error, 'ImageBase64Store')
    }

    setLoadingGetImage(false)
  }

  useEffect(() => {
    getBase64Image()
  }, [storeID])

  const handleUpload = async ({ file }) => {
    const ext = file.name.split('.').pop().toLocaleLowerCase()

    if (!['jpg', 'jpeg', 'png', 'gif', 'heic', 'webp'].includes(ext)) {
      ui.notiWarning('Please input an image')
      return
    }

    setLoadingUploadFile(true)

    try {
      let rawFile = file.originFileObj

      if (ext === 'heic') {
        rawFile = await heic2any({ blob: new Blob([rawFile], { type: rawFile.type }) })
      }

      let normalFile = await getNormalizedFile(rawFile)
      const uploadFile = normalFile.size > rawFile.size ? rawFile : normalFile
      const base64 = await fileToBase64(uploadFile)

      if (!storeID) {
        const itemRes = await addListItemService(lists.Base64Store, {
          Base64_Content: base64,
          ListSharepointName: listSharepointName
        })

        setStoreID(itemRes.ID)
      } else {
        await updateListItemService(lists.Base64Store, storeID, {
          Base64_Content: base64
        })

        setBase64Image(base64)
      }
    } catch (error) {
      handleError(error, 'handleUpload')
    }

    setLoadingUploadFile(false)
  }

  return (
    <div style={{ width: width }}>
      <div
        className="d-flex align-items-center justify-content-center"
        style={{ width: width, height: width, border: '1px solid #d9d9d9' }}>
        <Image
          style={{
            backgroundColor: 'rgb(246, 246, 246)',
            maxHeight: width
          }}
          src={loadingGetImage ? '/spinner.gif' : base64Image}
          fallback="data:image/png;base64,"
        />
      </div>
      {mode === formMode.edit ? (
        <div>
          <Upload
            customRequest={() => {
              return
            }}
            onChange={handleUpload}
            fileList={[]}>
            <Button
              block
              type="primary"
              ghost
              loading={loadingUploadFile}
              icon={<i className="fa-solid fa-arrow-up-from-bracket"></i>}
              style={{ whiteSpace: 'normal', height: 'auto', marginTop: 5 }}>
              Upload Image
            </Button>
          </Upload>
        </div>
      ) : null}
    </div>
  )
}

export default ImageBase64Store
